import { ChangeEvent, useEffect, useState } from "react"
import { getAllIssues } from "../../shared/api"
import { DataProjectIssue, State } from "../../types/types"
import IssuesList from "./IssuesList"
import { useDispatch, useSelector } from "react-redux";
import { setIssuesData } from "../../slices/slice";
import { Box, TextField, Button, Menu, Typography, FormGroup, FormControlLabel, Checkbox, Divider } from "@mui/material";
import { COLUMN_STATUSES } from "../../constants/constants";

const Main = () =>{
    const [data, setData] = useState<DataProjectIssue[]>(); //Все задачи
    const [filteredIssues, setFilteredIssues] = useState<DataProjectIssue[]>([]);

    const [statusFilter, setStatusFilter] = useState<string[]>([]);   // массив выбранных status
  const [boardFilter, setBoardFilter] = useState<number[]>([]);     // массив выбранных boardId

  // 3. Состояние для единого текстового поиска (по названию ИЛИ по исполнителю)
  const [searchQuery, setSearchQuery] = useState<string>("");

    const dispatch = useDispatch()
    const allIssues = useSelector((state:State)=>state.dataIssues.dataAllIssues)
    const boards = useSelector((state:State)=>state.dataBoards.boardsData)

    useEffect(() => {
      getAllIssues()
        .then((res) => {
            setData(res.data)
            if (res) dispatch(setIssuesData(res.data))}
        )
        .catch((err) => {
          console.error("Ошибка", err);
        });
    }, []);

    useEffect(() => {
      if (allIssues.length === 0) {
        setFilteredIssues([]);
        return;
      }
      const qLower = searchQuery.trim().toLowerCase();
      const result = allIssues.filter((issue) => {
        // 7.1. Фильтр по статусу (если хоть один статус выбран)
        if (statusFilter.length > 0 && !statusFilter.includes(issue.status)) {
          return false;
        }
        // 7.2. Фильтр по доске (boardId) (если хоть одна доска выбрана)
        if (boardFilter.length > 0 && !boardFilter.includes(issue.boardId)) {
          return false;
        }
        // 7.3. Текстовый поиск: если задано searchQuery, проверяем, что либо title, либо assignee содержит подстроку
        if (qLower) {
          const inTitle = issue.title.toLowerCase().includes(qLower);
          const inAssignee = issue.assignee.fullName.toLowerCase().includes(qLower);
          if (!inTitle && !inAssignee) {
            return false;
          }
        }
        return true;
      });
      setFilteredIssues(result);
    }, [allIssues, statusFilter, boardFilter, searchQuery]);

    const handleFilterButtonClick = (event: MouseEvent<HTMLButtonElement>) => {
      setAnchorEl(event.currentTarget);
    };
    const handleFilterMenuClose = () => {
      setAnchorEl(null);
    };

    // 9. Обработчики переключения чекбокса для статусов
    const toggleStatus = (status: string) => {
      setStatusFilter((prev) => {
        if (prev.includes(status)) {
          return prev.filter((s) => s !== status);
        } else {
          return [...prev, status];
        }
      });
    };

    // 10. Обработчики переключения чекбокса для досок
    const toggleBoard = (boardId: number) => {
      setBoardFilter((prev) => {
        if (prev.includes(boardId)) {
          return prev.filter((id) => id !== boardId);
        } else {
          return [...prev, boardId];
        }
      });
    };

    // 11. Обработчик для изменения строки поиска
    const handleSearchChange = (e: ChangeEvent<HTMLInputElement>) => {
      setSearchQuery(e.target.value);
    };

    // 12. Сброс всех фильтров
    const resetAllFilters = () => {
      setStatusFilter([]);
      setBoardFilter([]);
      setSearchQuery("");
    };

    return (
    <Box sx={{ p: 2 }}>
      {/* Блок с единой строкой поиска + кнопкой "Фильтры" */}
      <Box
        sx={{
          display: "flex",
          gap: 2,
          alignItems: "center",
          mb: 3,
          flexWrap: "wrap",
        }}
      >
        {/* 12.1. Единое текстовое поле для поиска по названию ИЛИ по исполнителю */}
        <TextField
          size="small"
          label="Поиск (название или исполнитель)"
          value={searchQuery}
          onChange={handleSearchChange}
          sx={{ minWidth: 250 }}
        />

        {/* 12.2. Кнопка открытия меню фильтров */}
        <Button
          variant="outlined"
          onClick={handleFilterButtonClick}
        >
          Фильтры
        </Button>

        {/* 12.3. Кнопка сброса всех фильтров */}
        <Button variant="text" onClick={resetAllFilters}>
          Сбросить всё
        </Button>
      </Box>

      {/* 13. Меню фильтров */}
      <Menu
        anchorEl={anchorEl}
        open={isFilterMenuOpen}
        onClose={handleFilterMenuClose}
        MenuListProps={{ sx: { p: 2 } }}
      >
        {/* 13.1. Секция: Статусы */}
        <Typography variant="subtitle1">Статус задачи</Typography>
        <FormGroup>
          {Object.entries(COLUMN_STATUSES).map(([key, obj]) => (
            <FormControlLabel
              key={key}
              control={
                <Checkbox
                  checked={statusFilter.includes(obj.status)}
                  onChange={() => toggleStatus(obj.status)}
                />
              }
              label={obj.title}
            />
          ))}
        </FormGroup>

        <Divider sx={{ my: 1 }} />

        {/* 13.2. Секция: Доски */}
        <Typography variant="subtitle1">Доска</Typography>
        <FormGroup>
          {boards.map((board) => (
            <FormControlLabel
              key={board.id}
              control={
                <Checkbox
                  checked={boardFilter.includes(board.id)}
                  onChange={() => toggleBoard(board.id)}
                />
              }
              label={board.name}
            />
          ))}
        </FormGroup>
      </Menu>

      {/* 14. Отображаем уже отфильтрованный список */}
      <IssuesList data={filteredIssues} />
    </Box>
  );
};
export default Main
