import { ChangeEvent, useEffect, useState } from "react";
import { getAllIssues } from "../../shared/api";
import { DataProjectIssue, State } from "../../types/types";
import IssuesList from "./IssuesList";
import { useDispatch, useSelector } from "react-redux";
import { setIssuesData } from "../../slices/slice";
import {
  Box,
  TextField,
  Button,
  Menu,
  Typography,
  FormGroup,
  FormControlLabel,
  Checkbox,
  Divider,
  Drawer,
  IconButton,
} from "@mui/material";
import { COLUMN_STATUSES } from "../../constants/constants";

const Main = () => {
  const [data, setData] = useState<DataProjectIssue[]>(); //Все задачи
  const [filteredIssues, setFilteredIssues] = useState<DataProjectIssue[]>([]);

  const [statusFilter, setStatusFilter] = useState<string[]>([]); // массив выбранных status
  const [boardFilter, setBoardFilter] = useState<number[]>([]); // массив выбранных boardId
  const [searchQuery, setSearchQuery] = useState<string>("");
  const [drawerOpen, setDrawerOpen] = useState(false);

  const dispatch = useDispatch();
  const allIssues = useSelector(
    (state: State) => state.dataIssues.dataAllIssues
  );
  const boards = useSelector((state: State) => state.dataBoards.boardsData);

  useEffect(() => {
    getAllIssues()
      .then((res) => {
        setData(res.data);
        if (res) dispatch(setIssuesData(res.data));
      })
      .catch((err) => {
        console.error("Ошибка", err);
      });
  }, []);

  useEffect(() => {
    if (allIssues.length === 0) {
      setFilteredIssues([]);
      return;
    }

    const qLower = searchQuery.trim().toLowerCase();

    const result = allIssues.filter((issue) => {
      // 7.1. Статусы
      if (statusFilter.length > 0 && !statusFilter.includes(issue.status)) {
        return false;
      }
      // 7.2. Доски
      if (boardFilter.length > 0 && !boardFilter.includes(issue.boardId)) {
        return false;
      }
      // 7.3. Текстовый поиск: title или assignee
      if (qLower) {
        const inTitle = issue.title.toLowerCase().includes(qLower);
        const inAssignee = issue.assignee.fullName
          .toLowerCase()
          .includes(qLower);
        if (!inTitle && !inAssignee) {
          return false;
        }
      }
      return true;
    });

    setFilteredIssues(result);
  }, [allIssues, statusFilter, boardFilter, searchQuery]);

  // 8. Открыть Drawer
  const handleDrawerOpen = () => {
    setDrawerOpen(true);
  };

  // 9. Закрыть Drawer
  const handleDrawerClose = () => {
    setDrawerOpen(false);
  };

  // 10. Переключение статуса в фильтрах
  const toggleStatus = (status: string) => {
    setStatusFilter((prev) =>
      prev.includes(status)
        ? prev.filter((s) => s !== status)
        : [...prev, status]
    );
  };

  // 11. Переключение доски в фильтрах
  const toggleBoard = (boardId: number) => {
    setBoardFilter((prev) =>
      prev.includes(boardId)
        ? prev.filter((id) => id !== boardId)
        : [...prev, boardId]
    );
  };

  // 12. Изменение поисковой строки
  const handleSearchChange = (e: ChangeEvent<HTMLInputElement>) => {
    setSearchQuery(e.target.value);
  };

  // 13. Сброс всех фильтров и поиска
  const resetAllFilters = () => {
    setStatusFilter([]);
    setBoardFilter([]);
    setSearchQuery("");
  };

  return (
    <Box sx={{ p: 2 }}>
      {/* Верхняя панель: поиск + кнопки */}
      <Box
        sx={{
          display: "flex",
          gap: 2,
          alignItems: "center",
          mb: 3,
          flexWrap: "wrap",
        }}
      >
        <TextField
          size="small"
          label="Поиск (название или исполнитель)"
          value={searchQuery}
          onChange={handleSearchChange}
          sx={{ minWidth: 250 }}
        />

        <Button variant="outlined" onClick={handleDrawerOpen}>
          Фильтры
        </Button>

        <Button variant="text" onClick={resetAllFilters}>
          Сбросить всё
        </Button>
      </Box>

      {/* 14. Drawer с фильтрами */}
      <Drawer
        anchor="right"
        open={drawerOpen}
        onClose={handleDrawerClose}
        // PaperProps={{ sx: { width: 280, p: 2 } }}
      >
        <Box sx={{ display: "flex", justifyContent: "space-between", mb: 1 }}>
          <Typography variant="h6">Фильтры</Typography>
          <Button onClick={handleDrawerClose}>
            Закрыть
          </Button>
        </Box>

        <Divider sx={{ mb: 2 }} />

        <Typography variant="subtitle1">Статус задачи</Typography>
        <FormGroup>
          {Object.entries(COLUMN_STATUSES).map(([key, obj]) => (
            <FormControlLabel
              key={key}
              control={
                <Checkbox
                  checked={statusFilter.includes(obj.status)}
                  onChange={() => toggleStatus(obj.status)}
                />
              }
              label={obj.title}
            />
          ))}
        </FormGroup>

        <Divider sx={{ my: 2 }} />

        <Typography variant="subtitle1">Доска</Typography>
        <FormGroup>
          {boards.map((board) => (
            <FormControlLabel
              key={board.id}
              control={
                <Checkbox
                  checked={boardFilter.includes(board.id)}
                  onChange={() => toggleBoard(board.id)}
                />
              }
              label={board.name}
            />
          ))}
        </FormGroup>
      </Drawer>

      {/* 15. Список задач */}
      <IssuesList data={filteredIssues} />
    </Box>
  );
};
export default Main;
