// src/components/filters/Filters.tsx

import { useEffect, useState, ChangeEvent } from "react";
import {
  Box,
  TextField,
  Button,
  Typography,
  FormGroup,
  FormControlLabel,
  Checkbox,
  Divider,
  Drawer,
} from "@mui/material";
import { DataProjectIssue, DataBoards } from "../../types/types";
import { COLUMN_STATUSES } from "../../constants/constants";

type FiltersProps = {
  issues: DataProjectIssue[];          // полный массив задач
  boards: DataBoards[];                // полный массив досок
  onFilteredChange: (filtered: DataProjectIssue[]) => void; // колбэк для передачи результата
};

const Filters = ({ issues, boards, onFilteredChange }: FiltersProps) => {
  // 1. Локальные состояния фильтров
  const [statusFilter, setStatusFilter] = useState<string[]>([]);
  const [boardFilter, setBoardFilter] = useState<number[]>([]);
  const [searchQuery, setSearchQuery] = useState<string>("");

  // 2. Управление Drawer
  const [drawerOpen, setDrawerOpen] = useState(false);

  // 3. При изменении issues или любого фильтра — пересчитываем отфильтрованный массив
  useEffect(() => {
    // если issues пуст или undefined, возвращаем пустой массив
    if (!issues || issues.length === 0) {
      onFilteredChange([]);
      return;
    }

    const qLower = searchQuery.trim().toLowerCase();

    const filtered = issues.filter((issue) => {
      // 3.1. Фильтр по статусу
      if (statusFilter.length > 0 && !statusFilter.includes(issue.status)) {
        return false;
      }

      // 3.2. Фильтр по доске
      if (boardFilter.length > 0 && !boardFilter.includes(issue.boardId)) {
        return false;
      }

      // 3.3. Текстовый поиск по названию или исполнителю
      if (qLower) {
        const inTitle = issue.title.toLowerCase().includes(qLower);
        const inAssignee = issue.assignee.fullName
          .toLowerCase()
          .includes(qLower);
        if (!inTitle && !inAssignee) {
          return false;
        }
      }

      return true;
    });

    onFilteredChange(filtered);
  }, [issues, statusFilter, boardFilter, searchQuery, onFilteredChange]);

  // 4. Обработчики для открытия/закрытия Drawer
  const handleDrawerOpen = () => setDrawerOpen(true);
  const handleDrawerClose = () => setDrawerOpen(false);

  // 5. Переключение статуса
  const toggleStatus = (status: string) => {
    setStatusFilter((prev) =>
      prev.includes(status) ? prev.filter((s) => s !== status) : [...prev, status]
    );
  };

  // 6. Переключение доски
  const toggleBoard = (boardId: number) => {
    setBoardFilter((prev) =>
      prev.includes(boardId) ? prev.filter((id) => id !== boardId) : [...prev, boardId]
    );
  };

  // 7. Изменение поисковой строки
  const handleSearchChange = (e: ChangeEvent<HTMLInputElement>) => {
    setSearchQuery(e.target.value);
  };

  // 8. Сброс всех фильтров и поиска
  const resetAllFilters = () => {
    setStatusFilter([]);
    setBoardFilter([]);
    setSearchQuery("");
  };

  return (
    <>
      {/* 9. Верхняя панель: единое поле поиска + кнопки */}
      <Box
        sx={{
          display: "flex",
          gap: 2,
          alignItems: "center",
          mb: 3,
          flexWrap: "wrap",
        }}
      >
        <TextField
          size="small"
          label="Поиск (название или исполнитель)"
          value={searchQuery}
          onChange={handleSearchChange}
          sx={{ minWidth: 250 }}
        />

        <Button variant="outlined" onClick={handleDrawerOpen}>
          Фильтры
        </Button>

        <Button variant="text" onClick={resetAllFilters}>
          Сбросить всё
        </Button>
      </Box>

      {/* 10. Drawer с чекбоксами фильтров */}
      <Drawer
        anchor="right"
        open={drawerOpen}
        onClose={handleDrawerClose}
        PaperProps={{ sx: { width: 280, p: 2 } }}
      >
        <Box sx={{ display: "flex", justifyContent: "space-between", mb: 1 }}>
          <Typography variant="h6">Фильтры</Typography>
          <Button onClick={handleDrawerClose}>Закрыть</Button>
        </Box>

        <Divider sx={{ mb: 2 }} />

        <Typography variant="subtitle1">Статус задачи</Typography>
        <FormGroup>
          {Object.entries(COLUMN_STATUSES).map(([key, obj]) => (
            <FormControlLabel
              key={key}
              control={
                <Checkbox
                  checked={statusFilter.includes(obj.status)}
                  onChange={() => toggleStatus(obj.status)}
                />
              }
              label={obj.title}
            />
          ))}
        </FormGroup>

        <Divider sx={{ my: 2 }} />

        <Typography variant="subtitle1">Доска</Typography>
        <FormGroup>
          {boards.map((board) => (
            <FormControlLabel
              key={board.id}
              control={
                <Checkbox
                  checked={boardFilter.includes(board.id)}
                  onChange={() => toggleBoard(board.id)}
                />
              }
              label={board.name}
            />
          ))}
        </FormGroup>
      </Drawer>
    </>
  );
};

export default Filters;
